mapper.update:
UPDATE reservation
SET id=#{id}, kakao=#{kakao}, nickname=#{nickname},
dogSize=#{dogSize},selectDate=#{selectDate},
timeSlot=#{timeSlot},cctv=#{cctv}
WHERE num=#{num}

mapper.select:
SELECT num, id, kakao, nickname,dogSize,selectDate,timeSlot,cctv
FROM reservation
WHERE num=#{num}

SELECT num, id, kakao, nickname, dogSize, selectDate, timeSlot, cctv
FROM reservation
WHERE id=#{id}
